mpat.js"></script>     <script src="https://www.gstatic.com/firebasejs/9.22.0/firebase-auth-compat.js"></script>     <script src="https://www.gstatic.com/firebasejs/9.22.0/firebase-firestore-compat.js"></script>     <script src="firebase-config.js"></script>     <script src="auth.js"></script>     <script src="script.js"></script>     <!-- Bootstrap 5 JS -->     <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/js/bootstrap.bundle.min.js"></script>      <script>         // Elements         const authPrompt = document.getElementById('auth-prompt');         const formContainer = document.getElementById('feedback-form-container');         const userDisplayName = document.getElementById('user-display-name');         const feedbackForm = document.getElementById('feedback-form');         const feedbackList = document.getElementById('feedback-list');         const loadingDiv = document.getElementById('loading-feedback');          // Check Auth State         firebase.auth().onAuthStateChanged((user) => {             if (user) {                 authPrompt.style.display = 'none';                 formContainer.style.display = 'block';                 userDisplayName.textContent = `Posting as: ${user.displayName || user.email}`;             } else {                 authPrompt.style.display = 'block';                 formContainer.style.display = 'none';             }         });          // Submit Feedback         feedbackForm.addEventListener('submit', async (e) => {             e.preventDefault();             const user = firebase.auth().currentUser;             if (!user) {                 alert('Please sign in first');                 return;             }              const message = document.getElementById('fb-message').value;              try {                 console.log("Preparing submission for user:", user.uid);                 // Adding a default rating of 5 internally to satisfy any existing Firestore validation rules                 const feedbackData = {                     name: user.displayName || user.email,                     message: message,                     userId: user.uid,                     rating: 5,                     timestamp: firebase.firestore.FieldValue.serverTimestamp()                 };                  console.log("Submission data:", feedbackData);                  await db.collection('feedbacks').add(feedbackData);                  console.log("Feedback added successfully!");                 alert('Thank you for your feedback!');                 feedbackForm.reset();             } catch (error) {                 console.error("DETAILED Error adding feedback: ", error);                 // Specifically check for common Firebase errors                 let userMessage = error.message;                 if (error.code === 'permission-denied') {                     userMessage = "Permission Denied. Please ensure you are logged in and the database rules allow this submission.";                 }                 alert('Error submitting feedback: ' + userMessage);             }         });          // Load Feedbacks         db.collection('feedbacks').orderBy('timestamp', 'desc').onSnapshot((querySnapshot) => {             feedbackList.innerHTML = '';             loadingDiv.style.display = 'none';              if (querySnapshot.empty) {                 feedbackList.innerHTML = '<p style="text-align:center; color:#888;">No feedbacks yet. Be the first to share your experience!</p>';                 return;             }              querySnapshot.forEach((doc) => {                 const data = doc.data();                 const date = data.timestamp ? data.timestamp.toDate().toLocaleDateString() : 'Just now';                  const item = document.createElement('div');                 item.className = 'card shadow-sm border-0 rounded-4 mb-4 feedback-item overflow-hidden';                 item.style.borderLeft = "5px solid var(--primary-yellow)";                 item.innerHTML = `                     <div class="card-body p-4">                         <div class="d-flex justify-content-between align-items-center mb-3">                             <span class="h5 fw-bold text-dark mb-0">${data.name}</span>                             <span class="badge bg-light text-muted fw-normal p-2 rounded-pill shadow-sm">                                 <i class="bi bi-clock me-1"></i> ${date}                             </span>                         </div>                         <p class="card-text text-secondary lh-lg fs-5 mb-0">${data.message}</p>                     </div>                 `;                 feedbackList.appendChild(item);             });         });     </script> </body>  </html>
