btn');              btn.disabled = true;             btn.textContent = 'Logging in...';              loginUser(email, password)                 .then((user) => {                     showAuthSuccess('Login successful! Redirecting...');                     setTimeout(() => {                         window.location.href = 'index.html';                     }, 1500);                 })                 .catch((error) => {                     console.error("Login error:", error);                     // Force generic error message as requested                     showAuthError('Incorrect credentials');                     btn.disabled = false;                     btn.textContent = 'Login';                 });         }          // Handle Google Sign In         function handleGoogleSignIn() {             hideAuthMessages();             signInWithGoogle()                 .then((user) => {                     if (user) {                         showAuthSuccess('Google login successful! Redirecting...');                         setTimeout(() => {                             window.location.href = 'index.html';                         }, 1500);                     }                 })                 .catch((error) => {                     console.error("Google Sign-In Error:", error);                 });         }          // Handle Google One Tap response         function handleCredentialResponse(response) {             console.log("One Tap Response received");             const credential = response.credential;             const googleCredential = firebase.auth.GoogleAuthProvider.credential(credential);              sessionStorage.setItem('isSocialLogin', 'true');             auth.signInWithCredential(googleCredential)                 .then((result) => {                     showAuthSuccess('Google login successful! Redirecting...');                     return syncUserToFirestore(result.user);                 })                 .then(() => {                     sessionStorage.removeItem('isSocialLogin');                     setTimeout(() => {                         window.location.href = 'index.html';                     }, 1500);                 })                 .catch((error) => {                     sessionStorage.removeItem('isSocialLogin');                     console.error("One Tap login error:", error);                     showAuthError('One Tap login failed');                 });         }          // Handle Forgot Password         function handleForgotPassword() {             const email = prompt('Enter your email address to reset password:');             if (email) {                 resetPassword(email)                     .then(() => {                         alert('Password reset email sent! Check your inbox.');                     })                     .catch((error) => {                         alert('Error: ' + error.message);                     });             }         }      </script> </body>  </html>
