 null;         const EMAILJS_SERVICE_ID = "service_4jef7dq";         const EMAILJS_TEMPLATE_ID = "template_vz6gzpl";          function goToStep(stepNumber) {             document.querySelectorAll('.signup-step').forEach(step => step.classList.remove('active'));             document.querySelectorAll('.step-dot').forEach(dot => dot.classList.remove('active'));              document.getElementById(`step-${stepNumber}`).classList.add('active');             for (let i = 1; i <= stepNumber; i++) {                 document.getElementById(`dot-${i}`).classList.add('active');             }         }          function handleSendOTP() {             const name = document.getElementById('signup-name').value;             const email = document.getElementById('signup-email').value;             const btn = document.getElementById('send-otp-btn');              if (!name || !email) {                 showAuthError("Please fill in your name and email.");                 return;             }              hideAuthMessages();             btn.disabled = true;             btn.textContent = 'Sending...';              // Generate 6-digit OTP             generatedOTP = Math.floor(100000 + Math.random() * 900000).toString();             console.log("OTP Generated:", generatedOTP); // For development              const templateParams = {                 to_name: name,                 to_email: email,                 otp: generatedOTP             };              emailjs.send(EMAILJS_SERVICE_ID, EMAILJS_TEMPLATE_ID, templateParams)                 .then(() => {                     showAuthSuccess("Verification code sent to " + email);                     document.getElementById('display-email').textContent = email;                     goToStep(2);                 })                 .catch(error => {                     console.error("EmailJS Error:", error);                     showAuthError("Failed to send OTP. Please try again.");                 })                 .finally(() => {                     btn.disabled = false;                     btn.textContent = 'Send OTP Code';                 });         }          function moveFocus(input, index) {             if (input.value.length === 1 && index < 6) {                 document.querySelectorAll('.otp-digit')[index].focus();             }         }          function handleBackspace(input, event) {             if (event.key === 'Backspace' && input.value.length === 0) {                 const digits = document.querySelectorAll('.otp-digit');                 const index = Array.from(digits).indexOf(input);                 if (index > 0) {                     digits[index - 1].focus();                 }             }         }          function handleVerifyOTP() {             const digits = document.querySelectorAll('.otp-digit');             let enteredOTP = "";             digits.forEach(d => enteredOTP += d.value);              if (enteredOTP.length < 6) {                 showAuthError("Please enter the full 6-digit code.");                 return;             }              if (enteredOTP === generatedOTP) {                 showAuthSuccess("Email verified successfully!");                 goToStep(3);             } else {                 showAuthError("Incorrect code. Please try again.");                 digits.forEach(d => d.value = "");                 digits[0].focus();             }         }          function handleFinalSignup(event) {             event.preventDefault();             hideAuthMessages();              const name = document.getElementById('signup-name').value;             const email = document.getElementById('signup-email').value;             const password = document.getElementById('signup-password').value;             const confirmPassword = document.getElementById('signup-confirm-password').value;             const btn = document.getElementById('signup-btn');              if (password !== confirmPassword) {                 showAuthError('Passwords do not match!');                 return;             }              if (password.length < 6) {                 showAuthError('Password must be at least 6 characters.');                 return;             }              btn.disabled = true;             btn.textContent = 'Creating Account...';              signUpUser(email, password, name)                 .then(() => {                     showAuthSuccess('Account created successfully! Redirecting...');                     setTimeout(() => {                         window.location.href = 'index.html';                     }, 2000);                 })                 .catch((error) => {                     console.error("Signup error:", error);                     showAuthError(error.message || 'Signup failed');                     btn.disabled = false;                     btn.textContent = 'Complete Registration';                 });         }          function handleGoogleSignIn() {             hideAuthMessages();             signInWithGoogle()                 .then((user) => {                     if (user) {                         showAuthSuccess('Google sign up successful! Redirecting...');                         setTimeout(() => {                             window.location.href = 'index.html';                         }, 1500);                     }                 })                 .catch((error) => {                     console.error("Google Sign-In Error:", error);                 });         }          // Handle Google One Tap response         function handleCredentialResponse(response) {             console.log("One Tap Response received");             const credential = response.credential;             const googleCredential = firebase.auth.GoogleAuthProvider.credential(credential);              sessionStorage.setItem('isSocialLogin', 'true');             auth.signInWithCredential(googleCredential)                 .then((result) => {                     showAuthSuccess('Google sign up successful! Redirecting...');                     return 